mapper.select:
SELECT u.user_id, u.username, u.password, u.role, u.created_at, u.updated_at, CASE WHEN u.role = 'student' THEN s.name WHEN u.role = 'counselor' THEN c.name ELSE NULL END as display_name, CASE WHEN u.role = 'student' THEN s.class_name ELSE NULL END as display_class_name, CASE WHEN u.role = 'student' THEN s.college ELSE NULL END as display_college, CASE WHEN u.role = 'student' THEN s.major ELSE NULL END as display_major FROM users u LEFT JOIN students s ON u.user_id = s.student_id LEFT JOIN counselors c ON u.user_id = c.counselor_id
AND u.role = #{role}
AND ( u.username LIKE CONCAT('%', #{keyword}, '%') OR s.name LIKE CONCAT('%', #{keyword}, '%') OR c.name LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY u.user_id ASC LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM users u LEFT JOIN students s ON u.user_id = s.student_id LEFT JOIN counselors c ON u.user_id = c.counselor_id
AND u.role = #{role}
AND ( u.username LIKE CONCAT('%', #{keyword}, '%') OR s.name LIKE CONCAT('%', #{keyword}, '%') OR c.name LIKE CONCAT('%', #{keyword}, '%') )
